CREATE TABLE `map` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '地图id',
  `area_id` int(3) unsigned NOT NULL DEFAULT '1' COMMENT '区域id',
  `name` varchar(1200) DEFAULT '' COMMENT '地图名称',
  `desc` varchar(200) DEFAULT '' COMMENT '地图描述',
  `image` varchar(12) DEFAULT '' COMMENT '地图图片',
  `event` varchar(12) DEFAULT '' COMMENT '事件操作',
  `task` varchar(12) DEFAULT '' COMMENT '地图任务',
  `lvl` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '地图等级',
  `yg_sx` int(30) NOT NULL DEFAULT '0' COMMENT '地图野怪刷新',
  `yg_ys` int(30) NOT NULL DEFAULT '0' COMMENT '地图怪物颜色',
  `shuxing` int(2) unsigned NOT NULL DEFAULT '0' COMMENT '地图属性',
  `npc` varchar(120) DEFAULT '' COMMENT 'npc数组',
  `yaoguai` varchar(240) DEFAULT '' COMMENT 'pet数组',
  `exit_b` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '出口北',
  `exit_x` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '出口西',
  `exit_d` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '出口东',
  `exit_n` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '出口南',
  `is_pk` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否可PK',
  `is_danrenfuben` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否单人副本',
  `is_duorenfuben` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否多人副本',
  `is_cunzhangjia` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否村长家',
  `is_fengyaoguan` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否封妖观',
  `is_yizhan` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否驿站',
  `is_zahuopu` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否杂货铺',
  `is_xiuxingchang` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否修行场',
  `is_jianyu` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否监狱',
  `is_gonghuilingdi` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否工会领地',
  `is_chalou` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否茶楼',
  `is_jingjichang` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否竞技场',
  `is_xs2` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '强化',
  `is_xs3` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '修补',
  `is_xs4` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '合成池',
  `is_xs5` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '强妖台',
  `is_xs6` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '拍卖',
  `is_xs7` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '存钱',
  `cs_map` int(1) NOT NULL DEFAULT '0' COMMENT '死亡传送地图',
  `ts_map` int(1) NOT NULL DEFAULT '1' COMMENT '特殊地图传送回城',
  `ts_pk` int(1) NOT NULL DEFAULT '0' COMMENT '特殊地图PK不红名',
  `npc_sx` int(10) NOT NULL DEFAULT '0' COMMENT 'npc刷新间隔时间',
  `npc_time` int(30) NOT NULL DEFAULT '0' COMMENT 'npc战败时间',
  `sxts_boss` int(10) NOT NULL DEFAULT '0' COMMENT '刷新特殊怪物',
  `kd` int(10) NOT NULL DEFAULT '0' COMMENT '宽度',
  `zd` int(10) NOT NULL DEFAULT '0' COMMENT '长度',
  `cj_id` int(10) NOT NULL DEFAULT '0' COMMENT '创建ID',
  UNIQUE KEY `id` (`id`)
