CREATE TABLE `npc` (
  `id` int(4) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `area_id` int(3) unsigned NOT NULL DEFAULT '1' COMMENT '区域',
  `name` varchar(200) DEFAULT '' COMMENT '名字',
  `tp` varchar(200) DEFAULT '' COMMENT '图片',
  `desc` varchar(100) DEFAULT '' COMMENT '描述',
  `sex` varchar(2) DEFAULT '男' COMMENT '性别',
  `talk` varchar(50) DEFAULT '他没有什么话想对你说。' COMMENT '对话',
  `pet_id` varchar(20) DEFAULT '' COMMENT '宠物种族数组',
  `pet_lvl` varchar(20) DEFAULT '' COMMENT '宠物等级数组',
  `is_lanlu` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否拦路角色',
  `is_yanwang` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否阎王',
  `is_cunzhangjia` tinyint(1) unsigned NOT NULL COMMENT '是否村长家',
  `is_fengyaoguan` tinyint(1) unsigned NOT NULL COMMENT '是否仓库',
  `is_yizhan` tinyint(1) unsigned NOT NULL COMMENT '是否驿站',
  `is_zahuopu` tinyint(1) unsigned NOT NULL COMMENT '是否杂货铺',
  `is_xiuxingchang` tinyint(1) unsigned NOT NULL COMMENT '是否修行场',
  `is_jianyu` tinyint(1) unsigned NOT NULL COMMENT '是否监狱',
  `is_gonghuilingdi` tinyint(1) unsigned NOT NULL COMMENT '是否工会领地',
  `is_chalou` tinyint(1) unsigned NOT NULL COMMENT '是否茶楼',
  `is_jingjichang` tinyint(1) unsigned NOT NULL COMMENT '是否竞技场',
  `is_xs2` tinyint(1) unsigned NOT NULL COMMENT '强化',
  `is_xs3` tinyint(1) unsigned NOT NULL COMMENT '修补',
  `is_xs4` tinyint(1) unsigned NOT NULL COMMENT '合成池',
  `is_xs5` tinyint(1) unsigned NOT NULL COMMENT '强妖台',
  `is_xs6` tinyint(1) unsigned NOT NULL COMMENT '拍卖',
  `is_xs7` tinyint(1) unsigned NOT NULL COMMENT '存钱',
  `is_xs8` int(11) NOT NULL COMMENT '购武器',
  `is_xs9` int(11) NOT NULL COMMENT '卖装备',
  `is_xs10` int(11) NOT NULL COMMENT '书店',
  `is_xs11` int(1) NOT NULL COMMENT '卖东西',
  `is_xs12` int(1) NOT NULL COMMENT '购买服装',
  `is_xs13` int(1) NOT NULL COMMENT '首饰',
  UNIQUE KEY `id` (`id`),
  UNIQUE KEY `name` (`name`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=100 DEFAULT CHARSET=utf8mb4 COMMENT='npc';

