CREATE TABLE `skill` (
  `id` int(4) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(600) DEFAULT '' COMMENT '名称',
  `miaoshu` varchar(500) DEFAULT '' COMMENT '描述',
  `shuxing` varchar(1) DEFAULT '' COMMENT '属性',
  `event` varchar(10) DEFAULT '' COMMENT '事件',
  `fangshi` int(1) unsigned NOT NULL DEFAULT '1' COMMENT '1直接攻击 2前置攻击',
  `leixing` int(1) unsigned NOT NULL DEFAULT '1' COMMENT '1普攻 2特攻 3防御 4其它',
  `weili` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '威力',
  `mingzhong` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '命中',
  `zhongji` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '暴击',
  `pp` int(2) unsigned NOT NULL DEFAULT '5' COMMENT 'PP',
  `lvl` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '等级',
  `exp` int(9) unsigned NOT NULL DEFAULT '0' COMMENT '经验',
  `mp` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '消耗蓝',
  `yczt` int(2) unsigned NOT NULL DEFAULT '0' COMMENT '异常状态',
  `ycztmz` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '异常状态命中',
  `min_lianji` int(2) unsigned NOT NULL DEFAULT '1' COMMENT '最小连击次数',
  `max_lianji` int(2) unsigned NOT NULL DEFAULT '1' COMMENT '最大连击次数',
  `in_map_use` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '在地图中使用',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`)
) ENGINE=InnoDB AUTO_INCREMENT=65 DEFAULT CHARSET=utf8mb4 COMMENT='技能';

