CREATE TABLE `task` (
  `id` int(4) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(200) DEFAULT '' COMMENT '名称',
  `mode` int(2) unsigned NOT NULL DEFAULT '0' COMMENT '任务模式 0主线 1支线',
  `lvl` int(3) unsigned NOT NULL DEFAULT '1' COMMENT '任务等级',
  `money` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '任务奖励铜板',
  `exp` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '任务奖励经验',
  `trigger_condition` varchar(255) DEFAULT '' COMMENT '触发条件',
  `from_map` int(6) unsigned NOT NULL DEFAULT '1' COMMENT '接受地图',
  `cs_pet_map` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '传送怪物地图',
  `cs_npc_map` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '传送npc地图',
  `cs_wp_map` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '传送物品地图',
  `from_npc` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '发布NPC',
  `from_desc` varchar(1000) DEFAULT '' COMMENT '接受描述',
  `from_item` varchar(100) DEFAULT '' COMMENT '接受物品',
  `from_item_count` varchar(100) DEFAULT '' COMMENT '接受物品数量',
  `from_prop` varchar(100) DEFAULT '' COMMENT '接受道具',
  `from_prop_star` varchar(100) DEFAULT '' COMMENT '接受道具星级',
  `from_prop_count` varchar(100) DEFAULT '' COMMENT '接受道具数量',
  `to_map` int(6) unsigned NOT NULL DEFAULT '1' COMMENT '完成地图',
  `to_npc` int(4) unsigned NOT NULL DEFAULT '0' COMMENT '完成NPC',
  `to_desc` varchar(1000) DEFAULT '' COMMENT '完成描述',
  `to_item` varchar(100) DEFAULT '' COMMENT '完成物品',
  `to_item_count` varchar(100) DEFAULT '' COMMENT '完成物品数量',
  `to_prop` varchar(100) DEFAULT '' COMMENT '完成道具',
  `to_prop_star` varchar(100) DEFAULT '' COMMENT '完成道具星级',
  `to_prop_count` varchar(100) DEFAULT '' COMMENT '完成道具数量',
  `zhuxian` int(4) unsigned NOT NULL DEFAULT '0' COMMENT '任务主线编号',
  `leixing` int(2) unsigned NOT NULL DEFAULT '0' COMMENT '任务类型 0普通 1藏妖 2拥有 3掉落',
  `item` varchar(100) DEFAULT '' COMMENT '任务物品',
  `item_count` varchar(100) DEFAULT '' COMMENT '任务物品数量',
  `pet` varchar(100) DEFAULT '' COMMENT '任务宠物',
  `pet_count` varchar(100) DEFAULT '' COMMENT '任务宠物数量',
  `pet_lvl` varchar(100) DEFAULT '' COMMENT '任务宠物等级',
  `npc` varchar(100) DEFAULT '' COMMENT '任务NPC',
  `npc_count` varchar(100) DEFAULT '' COMMENT '任务NPC数量',
  `is_xunhuan` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否循环任务',
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8mb4 COMMENT='任务';

